import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [usernameDirty, setUsernameDirty] = useState(false)
    const [passwordDirty, setPasswordDirty] = useState(false)
    const [usernameError, setUsernameError] = useState('Логин не может быть пустым')
    const [passwordError, setPasswordError] = useState('Пароль не может быть пустым')
    const navigate = useNavigate();



    const handleLogin = async(e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:8000/auth/api/token/', { username, password });
            localStorage.setItem('access_token', response.data.access);
            localStorage.setItem('refresh_token', response.data.refresh);
            navigate('/home');
        } catch (err) {
            alert('An error occurred. Please try again.');
        }
    }

    const handleRegister = async(e) => {
        e.preventDefault();
        try {
            navigate('/register');
        } catch (error) {
            alert('Page Not Found');
        }
    };

    const handleForgot = async(e) => {
        e.preventDefault();
        try {
            navigate('/reset-password');
        } catch (error) {
            alert('Page Not Found');
        }
    };

    const blurHandler = (e) => {
        switch (e.target.name) {
            case 'username':
                setUsernameDirty(true)
                break
            case 'password':
                setPasswordDirty(true)
                break
        }

    }

    const usernameHandler = (e) => {
        setUsername(e.target.value)
        const re = /\w*[a-zA-Z]\w*/;
        if (!re.test(String(e.target.value))){
            setUsernameError("Некорректное значение")
        }
        else {
            setUsernameError("")
        }
    }

    return ( 
        <div className="login-form">
            <h2> Войти </h2> 
            <form onSubmit={handleLogin}>
                <div className="form-group">
                    <label className="form-label"> Логин </label>
                    {(usernameDirty && usernameError) && <div style={{ color:'#D33F49' }}>{usernameError}</div>}
                    <input onBlur={e => blurHandler(e)} type="text" className="form-control" name='username' value={username} onChange={(e) => setUsername(e.target.value)} required />
                </div>

                <div className="form-group">
                    <label className="form-label"> Пароль </label>
                    {(passwordDirty && passwordError) && <div style={{color:'#D33F49'}}> {passwordError} </div>}
                    <input onBlur={e => blurHandler(e)} type="password" className="form-control" name='password' value={password} onChange={(e) => setPassword(e.target.value)} required/>
                </div>
                    <button type="submit" className="log-btn"> Войти </button>

                    <a href="{'/register'}" onClick={handleRegister} className="link register-link"> Регистрация </a> 
                    <a href="{'/reset-password'}" onClick={handleForgot} className="link forgot-link"> Забыли пароль? </a> 
                    </form> 
                    </div>
                );
            }

            export default Login;
