import axios from 'axios';
import {handleLogin, handleRegister} from './Login';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';



function ResetPassword(){
    const [email, setEmail] = useState('');

    const handleResetPassword = (e) => {
        const response = axios.post('http://localhost:8000/auth/password-reset/', { email });
        navigate('/home');
    }

    return (
        <div className="login-form">
            <h2> Сброс пароля </h2>
            <form onSubmit={handleResetPassword}>
                <div className="form-group">
                    <label className="form-label"> Введите е-mail </label>
                    <input type="email" className="form-control" name='email' value={email} onChange={(e) => setEmail(e.target.value)} required />
                </div>

                <button type="submit" className="log-btn"> Отправить </button>

                <a href="{'/register'}" onClick={handleRegister} className="link register-link"> Регистрация </a>
                <a href="{'/login'}" onClick={handleLogin} className="link forgot-link"> Войти </a>
            </form>
        </div>
    )
}
